
public class ArithmeticExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numberOne = 2;
		int theBestNumber = 100;
		
		numberOne += 10;
		numberOne--;
		
		theBestNumber *= numberOne;
		
		theBestNumber++;
		
		System.out.println("Number One: " + numberOne);
		System.out.println("The best number: " + theBestNumber);
	}

}
